import Posts from "./Posts/Posts";
import { useState } from 'react';
import NewPost from "../Components/NewPost/";

export default function Dashboard() {

    let i = 4;
    const [postsState, setPostsState] = useState(
        [
            { id: 1, title: "Angular", author: "abdelaziz" },
            { id: 2, title: "React", author: "aboukhame" },
            { id: 3, title: "Nodejs", author: "John" }
        ]
    );

    const [postState, setPostState] = useState(
        {
            title: "",
            author: ""
        }
    )

    const onChange = (events) => {
        const copy = { ...postState };
        copy[events.target.title] = events.target.value;
        setPostState(copy);
    }

    const addButtonClicked = () => {
        const copy = { ...postState };
        copy.id = i;
        i++;
        const copyPostsState = [...postsState]
        copyPostsState.push(copy);
        setPostsState(copyPostsState);
    }

    return (
        <div>
            <Posts posts={postsState} />
            <NewPost
                title={postState.title}
                author={productState.author}
                onChange={(event) => { onChange(event) }}
                addButtonClicked={addButtonClicked}
            />
        </div>
    )

}
